    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(formData.email)) {
      setMessage({
        text:
          lang === "ar"
            ? "يرجى إدخال بريد إلكتروني صحيح"
            : "Please enter a valid email address",
        type: "error",
      });
      return;
    }

